package chessgame;

import chessgame.entities.*;
import chessgame.windows.*;

public class App {
    public int run() {
        Window wnd = new Menu().go();

        while(wnd != null){
            wnd = wnd.go();
        }

        return 0;
    }

    public static void main(String[] args) {
        new App().run();
    }
}
